allow:
Bash(php -r "
// Test the referer validation logic
$home_url = ''http://example.com'';
$test_cases = [
    ''http://example.com:8080'',
    ''https://example.com'',
    ''http://example.com.evil.com'',
    ''http://evil.com'',
    ''http://example.com/admin'',
];

foreach ($test_cases as $referer) {
    $referer_parsed = parse_url($referer);
    $home_parsed = parse_url($home_url);

    $match = isset($referer_parsed[''host'']) && 
             isset($home_parsed[''host'']) && 
             $referer_parsed[''host''] === $home_parsed[''host''];

    echo $referer . '' => '' . ($match ? ''ALLOWED'' : ''BLOCKED'') . PHP_EOL;
}
")
